import "./CartWidget.css"
const CartWidget = (props) => {
    let {nombreP,descP,precioP,imgP} = props;

  return (
    <div class="productoItem">
        <img src={imgP} alt="" />
        <h4>{nombreP}</h4>
        <p>{descP}</p>
        <p>{precioP}</p>

        <div>
          <button>-</button>
          <input type="text" value="1"/>
          <button>+</button>
        </div>
        
        <button>agregar a carrito</button>
    </div>
  );
}

export default CartWidget;
